n:&lt;/label&gt;\r\n                &lt;textarea id=&quot;found-description&quot; name=&quot;found-description&quot; placeholder=&quot;Describe the item&quot; required&gt;&lt;/textarea&gt;\r\n\r\n                &lt;label for=&quot;found-location&quot;&gt;Found Location:&lt;/label&gt;\r\n                &lt;input type=&quot;text&quot; id=&quot;found-location&quot; name=&quot;found-location&quot; placeholder=&quot;e.g., Central Mall&quot; required&gt;\r\n\r\n                &lt;label for=&quot;found-date&quot;&gt;Date:&lt;/label&gt;\r\n                &lt;input type=&quot;date&quot; id=&quot;found-date&quot; name=&quot;found-date&quot; required&gt;\r\n\r\n                &lt;button type=&quot;submit&quot;&gt;Submit Found Item&lt;/button&gt;\r\n            &lt;/form&gt;\r\n        &lt;/section&gt;\r\n\r\n        &lt;!-- View Items Section --&gt;\r\n        &lt;section id=&quot;list&quot; class=&quot;list-section&quot;&gt;\r\n            &lt;h2&gt;Items List&lt;/h2&gt;\r\n            &lt;p&gt;Select a category to filter items:&lt;/p&gt;\r\n            &lt;select id=&quot;item-filter&quot;&gt;\r\n                &lt;option value=&quot;all&quot;&gt;All Items&lt;/option&gt;\r\n                &lt;option value=&quot;lost&quot;&gt;Lost Items&lt;/option&gt;\r\n                &lt;option value=&quot;found&quot;&gt;Found Items&lt;/option&gt;\r\n            &lt;/select&gt;\r\n\r\n            &lt;div class=&quot;item-list&quot;&gt;\r\n                &lt;!-- Example Items --&gt;\r\n                &lt;div class=&quot;item&quot;&gt;\r\n                    &lt;h3&gt;Black Wallet&lt;/h3&gt;\r\n                    &lt;p&gt;&lt;strong&gt;Category:&lt;/strong&gt; Documents&lt;/p&gt;\r\n                    &lt;p&gt;&lt;strong&gt;Location:&lt;/strong&gt; City Park&lt;/p&gt;\r\n                    &lt;p&gt;&lt;strong&gt;Status:&lt;/strong&gt; Lost&lt;/p&gt;\r\n                &lt;/div&gt;\r\n                &lt;div class=&quot;item&quot;&gt;\r\n                    &lt;h3&gt;Red Backpack&